INSERT INTO `film_comment` VALUES ('1133261450', '26387939', '父亲价值观简直令人作呕，以梦想、金钱、冠军强制女儿人生，所谓父女情不如说是满足私欲后的奖励。电影讲述打破性别偏见，其实自己已经偏见得无可救药。纵然父权被击倒在地，也要用整个后半部分将其再次扶到最高位。', '2', '979', 'lephemera', '2744437', '蜉蝣', '2017-01-07 02:10:25');
